import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Container,
  Row,
  Col,
  Badge,
  Media,
  Table,
  UncontrolledTooltip,
} from "reactstrap";
import UserHeader from "components/Headers/UserHeader.js";
import React, { useEffect, useState } from "react";
import { post } from "services/services";
import { SuccessToast } from "Helper/Toast";
import { ErrorToast } from "Helper/Toast";
import { ApiURL } from "services/apiConstants";
import { get } from "services/services";


const TodayResultScreen = () => {
  const [drop, setDrop] = useState('')
  const [resultTime, setResultTime] = useState('')
  const [resultList, setResultList] = useState([])

  const getResult_List = () => {
    get(ApiURL.game_List).then((res) => {
      if (res && res?.status === true) {
        setResultList(res?.data)
      }
    })
  }
  useEffect(() => {
    getResult_List()
  }, [])

  const addGame = () => {

  };



  const handleSubmit = () => {
    if (checkValidation() == false) {
      return;
    } else {
      addGame();
    }
  };

  const checkValidation = () => {
    if (drop == '') {
      ErrorToast('Game Name is required !');
      return false;
    } else if (resultTime == '') {
      ErrorToast('Result Time is required !');
      return false;
    }
  };


  return (
    <>
      <UserHeader />
      <Container className="mt--7" fluid>
        <Row>
          <Col className="order-xl-1" xl="12">
            <Card className="bg-secondary shadow">
              <CardHeader className="bg-white border-0">
                <Row className="align-items-center">
                  <Col xs="8">
                    <h3 className="mb-0">Add Games Result</h3>
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                <Form>
                  <h6 className="heading-small text-muted mb-4">
                    User Add Games Result information
                  </h6>
                  <div className="pl-lg-4">
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-gameName"
                          >
                            Game Name
                          </label>
                          <Input
                            id="exampleSelect"
                            name="status"
                            type="select"
                            value={drop}
                            onChange={(e) => { setDrop(e.target.value) }}
                          >
                            <option value={""} selected disabled>Game Name Selected</option>
                            {resultList.map((item) => {
                              return (
                                <option value={item?._id}>
                                  {item?.name}
                                </option>
                              )

                            })}

                          </Input>
                        </FormGroup>

                      </Col>

                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-number"
                          >
                            Date
                          </label>
                          <Input
                            className="form-control-alternative"
                            id="input-number"
                            placeholder="Result"
                            type="date"
                            onChange={(e) => { setResultTime(e.target.value) }}
                            value={resultTime}
                          />
                        </FormGroup>
                      </Col>
                    </Row>

                    <Button
                      color="info"
                      href="#pablo"
                      onClick={handleSubmit}
                      size="lg"
                      style={{ alignSelf: 'center' }}
                    >
                      Today Result
                    </Button>
                  </div>
                </Form>

              </CardBody>
            </Card>
            <Table className="align-items-center" responsive>
              <thead className="thead-light">
                <tr>
                  <th scope="col">Name</th>
                  <th scope="col">Result Time</th>
                  <th scope="col">Start Time</th>
                  <th scope="col">End Time</th>
                  <th scope="col">Action</th>
                  <th scope="col" />
                </tr>
              </thead>
              {
                resultList?.map((item, i) => {
                  return (
                    <tbody>
                      <tr>
                        <th scope="row">
                          <Media className="align-items-center">
                            <Media>
                              <span className="mb-0 text-sm">{item?.name}</span>
                            </Media>
                          </Media>
                        </th>
                        <td>{item?.resultTime}</td>
                        <td>
                          <Badge color="" className="badge-dot mr-4">
                            {item?.startTime}
                          </Badge>
                        </td>
                        <td>
                          <Badge color="green" className="badge-dot mr-4">
                            {item?.endTime}
                          </Badge>
                        </td>
                        <td>
                          <Badge color="red" className="badge-dot mr-4">
                            <i className="ni ni-fat-remove" data-placement="top" id="tooltip611234743" style={{ fontSize: "30px" }} onClick={() => handleDelete(item)}></i>
                            <UncontrolledTooltip
                              delay={0}
                              placement="top"
                              target="tooltip611234743"
                            >
                              Delete
                            </UncontrolledTooltip>
                          </Badge>
                          <Badge color="green" className="badge-dot mr-4">
                            <i className="ni ni-ruler-pencil" data-placement="top" id="tooltip611234744" style={{ fontSize: "15px" }} onClick={() => toggleModal(item)}></i>
                            <UncontrolledTooltip
                              delay={0}
                              placement="top"
                              target="tooltip611234744"
                            >
                              Edit
                            </UncontrolledTooltip>
                          </Badge>
                        </td>
                      </tr>
                    </tbody>
                  )
                })
              }
            </Table >
          </Col>
        </Row >
      </Container >
    </>
  );
};

export default TodayResultScreen;
