import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  Container,
  Row,
  Col,
} from "reactstrap";
import UserHeader from "components/Headers/UserHeader.js";
import React, { useState } from "react";
import { post } from "services/services";
import { SuccessToast } from "Helper/Toast";
import { ErrorToast } from "Helper/Toast";
import { ApiURL } from "services/apiConstants";
import { useNavigate } from "react-router-dom";

const AddGameResultScreen = () => {
  const navigate = useNavigate();
  const [name, setName] = useState('')
  const [resultTime, setResultTime] = useState('')



  const addGame = () => {
    const formData = {
      name: name,
      resultTime: resultTime,
    };
    post(ApiURL.add_game, formData).then(res => {
      if (res && res?.status == true) {
        SuccessToast(res?.message)
      } else {
        ErrorToast(res?.message)
      }
    });
  };

  const handleSubmit = () => {
    if (checkValidation() == false) {
      return;
    } else {
      addGame();
    }
  };

  const checkValidation = () => {
    if (name == '') {
      ErrorToast('Name is required !');
      return false;
    } else if (resultTime == '') {
      ErrorToast('result Time is required !');
      return false;
    }
  };


  return (
    <>
      <UserHeader />
      <Container className="mt--7" fluid>
        <Row>
          <Col className="order-xl-1" xl="12">
            <Card className="bg-secondary shadow">
              <CardHeader className="bg-white border-0">
                <Row className="align-items-center">
                  <Col xs="8">
                    <h3 className="mb-0">Add Games Result</h3>
                  </Col>
                </Row>
              </CardHeader>
              <CardBody>
                <Form>
                  <h6 className="heading-small text-muted mb-4">
                    User Add Games Result information
                  </h6>
                  <div className="pl-lg-4">
                    <Row>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-gameName"
                          >
                            Game Name
                          </label>
                          <Input
                            className="form-control-alternative"
                            defaultValue="Kuber"
                            id="input-GameName"
                            placeholder="Game Name"
                            type="dropdown"
                            onChange={(e) => { setName(e.target.value) }}
                            value={name}
                          />
                        </FormGroup>
                      </Col>
                      <Col lg="6">
                        <FormGroup>
                          <label
                            className="form-control-label"
                            htmlFor="input-number"
                          >
                            Result Time
                          </label>
                          <Input
                            className="form-control-alternative"
                            id="input-number"
                            placeholder="Result"
                            type="time"
                            onChange={(e) => { setResultTime(e.target.value) }}
                            value={resultTime}
                          />
                        </FormGroup>
                      </Col>
                    </Row>

                    <Button
                      color="info"
                      href="#pablo"
                      onClick={handleSubmit}
                      size="lg"
                      style={{ alignSelf: 'center' }}
                    >
                      Game Result
                    </Button>
                  </div>
                </Form>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default AddGameResultScreen;
